// TODO: 매개변수를 받아서 최소값을 반환
function getMin(a, b){
    if (a > b) {
        return b
    } else {
        return a
    }
}

console.log(getMin(3, 5))

function getMin2(a, b){
    var min = Infinity
    if (a > b) {
        min = b
    }
    min = a
    return min
}
console.log(getMin2(2, 3))
// get_min_from_array : snake_case, dash_case
// 함수 선언식
// 1. 함수를 선언한다.
function getMinFromArray(numbers){ // lowerCamelcase
    var min = Infinity
    for (num of numbers){
        // 삼항연산자
        // min = (조건) ? true 일 때 : false 일 때
        // if (min > num){
        //      min = num
        // }
        min = min > num ? num : min
    }
    return min
}
// 2. 익명함수를 사용하여 함수를 선언하고 변수에 저장한다.
// var getMinFromArray = function(){}
console.log(typeof getMinFromArray)

console.log(getMinFromArray([1, 2, 3, 4, 5]))